<?php
CLASS TEMPLATE {
    function tpl($pagename)
    {
      $pg = VIEW.'inc/'.$pagename;  
        if(file_exists($pg))
        {
            include($pg);
        }
    }


    function subpg($pagename)
    {
      $pg = VIEW.'subpage/'.$pagename;  
        if(file_exists($pg))
        {
            include($pg);
        }
    }
    
    function widget($name,$type,$gettype="echo")
    {
        $gpg = new TSMYSQL;
        $gpg->connect_ts();
        $widget = $gpg->query_ts("SELECT content  FROM `widgets` WHERE `name` LIKE '$name' AND `type` LIKE '$type'");
        $gpg->disconnect_ts();
        if(COUNT($widget)>0)
        {
            $content = base64_decode($widget[0]['content']);
        }
        if($gettype!="echo")
        {
            return $content;
        }
        else
        {
            echo $content;
        }
    }
    

function getDataURI($image, $mime = '') {
	return 'data: '.(function_exists('mime_content_type') ? mime_content_type($image) : $mime).';base64,'.base64_encode(file_get_contents($image));
}
    
    function img($imagepath)
    {
      if(file_exists($imagepath))
      {     
       echo  self::getDataURI($imagepath); 
      }
      else
      {
        $noimg = 'data:image/jpeg;base64,';        
        echo  $noimg;  
      }
    }    
}
?>